USE `log`;CREATE TABLE `t_log_play_keep` (  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键',  `time` varchar(50) NOT NULL COMMENT '时间',  `registered` tinyint(50) NOT NULL COMMENT '注册数',  `keep_one` varchar(50) DEFAULT NULL COMMENT '昨日留存',  `keep_two` varchar(50) DEFAULT NULL COMMENT '2日留存',  `keep_three` varchar(50) DEFAULT NULL COMMENT '3日留存',  `keep_four` varchar(50) DEFAULT NULL COMMENT '4日留存',  `keep_five` varchar(50) DEFAULT NULL COMMENT '5日留存',  `keep_six` varchar(50) DEFAULT NULL COMMENT '6日留存',  `keep_seven` varchar(50) DEFAULT NULL COMMENT '7日留存',  `keep_eight` varchar(50) DEFAULT NULL COMMENT '8日留存',  `keep_nine` varchar(50) DEFAULT NULL COMMENT '9日留存',  `keep_ten` varchar(50) DEFAULT NULL COMMENT '10日留存',  `crate_time` datetime NOT NULL COMMENT '创建时间',  PRIMARY KEY (`id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8